import React, { Component, Fragment } from "react";
import { Container, Row, Col } from "react-bootstrap";
import graphicslogo from "../../asset/image/graphics.svg";
import weblogo from "../../asset/image/mobile.svg";
import mobilelogo from "../../asset/image/web.svg";
import AppUrl from "../../RestApi/AppUrl";
import RestClient from "../../RestApi/RestClient";
export default class Services extends Component {
  constructor() {
    super();
    this.state = {
      myData: [],
    };
  }
  componentDidMount() {
    RestClient.GetRequest(AppUrl.Service).then((result) => {
      this.setState({ myData: result });
    });
  }
  render() {
    const myList = this.state.myData;
    myList.map((myList) => {
      return (
        <Col lg={4} md={6} sm={12}>
          <div className="serviceCard text-center">
            <img src={weblogo} />
            <h2 className="serviceName">Web Development</h2>
            <p className="serviceDescription">
              I design and develop static and dynamic web sites as per your
              requirements as we believe, “web is world’s next home”.
            </p>
          </div>
        </Col>
      );
    });
    return (
      <Fragment>
        <Container className="text-center">
          <h1 className="serviceMainTitle">MY SERVICES</h1>
          <Row></Row>
        </Container>
      </Fragment>
    );
  }
}
