import axios from "axios";
class RestClient {
  static GetRequest(getUrl) {
    return axios
      .get(getUrl)
      .then((response) => {
        return response.data;
      })
      .catch((error) => {
        return null;
      });
  }
  static PostRequest = (PostUrl, PostJson) => {
    let config = {
      headers: { "Content-type": "application/json" },
    };
    return axios
      .post(PostUrl, PostJson)
      .then((response) => {
        return response.data;
      })
      .catch((error) => {
        return null;
      });
  };
}
export default RestClient;
