import React, { Component, Fragment } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import AppUrl from "../../RestApi/AppUrl";
import RestClient from "../../RestApi/RestClient";
import loading from "../loading/loading";
export default class AllCourses extends Component {
  constructor() {
    super();
    this.state = {
      myData: [],
      loading: true,
    };
  }
  componentDidMount() {
    RestClient.GetRequest(AppUrl.CourseAll).then((result) => {
      this.setState({ myData: result, loading: false });
    });
  }
  render() {
    if (this.state.loading == true) {
      return <loading />;
    }
    const myList = this.state.myData;
    const myView = myList.map((myList) => {
      return (
        <Col lg={6} md={12} sm={12} className="p-2">
          <Row>
            <Col lg={6} md={6} sm={12}>
              <img className="courseImg" src={myList.short_img} />
            </Col>
            <Col lg={6} md={6} sm={12}>
              <h5 className=" courseTitle text-justify">
                {myList.short_title}
              </h5>
              <p className=" courseDes text-justify">{myList.short_des}</p>
              <Link
                className="courseDetails float-left"
                to={"/CourseDetails/" + myList.id}
              >
                Details
              </Link>
            </Col>
          </Row>
        </Col>
      );
    });
    return (
      <Fragment>
        <Container className="text-center">
          <h1 className="serviceMainTitle">OUR COURSES</h1>
          <Row>{myView}</Row>
        </Container>
      </Fragment>
    );
  }
}
