import React, { Component, Fragment } from "react";
import { Container, Row, Col } from "react-bootstrap";
import RestClient from "../../RestApi/RestClient";
import ReactHtmlParser from "react-html-parser";
export default class TermsDescription extends Component {
  constructor() {
    super();
    this.state = {
      desc: "",
    };
  }
  componentDidMount() {
    RestClient.GetRequest(AppUrl.Information).then((result) => {
      this.setState({ desc: result[0]["about"] });
    });
  }
  render() {
    return (
      <Fragment>
        <Container className="mt-5">
          <Row>
            <Col sm={12} md={12} lg={12}>
              <p className="serviceDescription">
                BY ACCESSING OR USING THIS SITE YOU REPRESENT THAT YOU HAVE THE
                FULL AUTHORITY TO ACT TO BIND YOURSELF, ANY THIRD PARTY,
                COMPANY, OR LEGAL ENTITY, AND THAT YOUR USE AND/OR INTERACTION,
                AS WELL AS CONTINUING TO USE OR INTERACT, WITH THE SITE
                CONSTITUTES YOUR HAVING READ AND AGREED TO THESE TERMS OF USE AS
                WELL AS OTHER AGREEMENTS THAT WE MAY POST ON THE SITE.
              </p>
            </Col>
          </Row>
        </Container>
      </Fragment>
    );
  }
}
