import React, { Component, Fragment } from "react";
import "../../asset/css/custom.css";
import "../../asset/css/bootstrap.min.css";
import { Nav, Navbar } from "react-bootstrap";
import { NavLink } from "react-router-dom";

export default class TopNavigation extends Component {
  constructor() {
    super();
    this.state = {
      navBarTitle: "navTitle",
      navVariant: "dark",
      navBarBack: "navBackground",
      navBarItem: "navItem",
    };
  }

  onScroll = () => {
    if (window.scrollY > 100) {
      this.setState({
        navVariant: "light",
        navBarTitle: "navTitleScroll",
        navBarBack: "navBackgroundScroll",
        navBarItem: "navItemScroll",
      });
    } else if (window.scrollY < 100) {
      this.setState({
        navVariant: "dark",
        navBarTitle: "navTitle",
        navBarBack: "navBackground",
        navBarItem: "navItem",
        pageTitle: props.title,
      });
    }
  };

  componentDidMount() {
    window.addEventListener("scroll", this.onScroll);
  }
  render() {
    return (
      <Fragment>
        <Navbar
          variant={this.state.navVariant}
          className={this.state.navBarBack}
          fixed="top"
          collapseOnSelect
          expand="lg"
        >
          <Navbar.Brand className={this.state.navBarTitle}>Kibo</Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto"></Nav>
            <Nav>
              <Nav.Link>
                <NavLink className={this.state.navBarItem} to="/">
                  HOME
                </NavLink>
              </Nav.Link>

              <Nav.Link>
                <NavLink
                  exact
                  activeStyle={{ color: "#00a8ee" }}
                  className={this.state.navBarItem}
                  to="/service"
                >
                  SERVICES
                </NavLink>
              </Nav.Link>
              <Nav.Link>
                <NavLink
                  exact
                  activeStyle={{ color: "#00a8ee" }}
                  className={this.state.navBarItem}
                  to="/course"
                >
                  COURSES
                </NavLink>
              </Nav.Link>
              <Nav.Link>
                <NavLink
                  exact
                  activeStyle={{ color: "#00a8ee" }}
                  className={this.state.navBarItem}
                  to="/portfolio"
                >
                  PORTFOLIO
                </NavLink>
              </Nav.Link>

              <Nav.Link>
                <NavLink
                  exact
                  activeStyle={{ color: "#00a8ee" }}
                  className={this.state.navBarItem}
                  to="/contact"
                >
                  CONTACT
                </NavLink>
              </Nav.Link>

              <Nav.Link>
                <NavLink
                  exact
                  activeStyle={{ color: "#00a8ee" }}
                  className={this.state.navBarItem}
                  to="/about"
                >
                  ABOUT
                </NavLink>
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </Fragment>
    );
  }
}
